(392, 'Đồng hồ Nam G-Shock DW-6900BBA-1DR', 3, 32, '<h3>Thiết kế hầm hố v&agrave; c&aacute; t&iacute;nh d&agrave;nh cho c&aacute;c bạn nam năng động</h3>\r\n<p><a title=\"Đồng hồ nam G-Shock DW-6900BBA-1DR ch&iacute;nh h&atilde;ng, gi&aacute; rẻ, b&aacute;n tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dong-ho-deo-tay/g-shock-dw-6900bba-1dr-nam\" target=\"_blank\">Đồng hồ G-Shock DW-6900BBA-1DR</a>&nbsp;l&agrave; chiếc&nbsp;<a title=\"Đồng hồ nam ch&iacute;nh h&atilde;ng, gi&aacute; rẻ, b&aacute;n tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dong-ho-deo-tay-nam\" target=\"_blank\">đồng hồ nam</a>&nbsp;thuộc d&ograve;ng đồng hồ G-Shock, đến từ thương hiệu Casio - Nhật Bản uy t&iacute;n v&agrave; l&acirc;u đời.</p>', 1, 3455000, 'ee35495bb1.jpg'),
(393, 'Đồng hồ Nam G-Shock DW-5700BBM-2DR', 3, 32, '<h3>Trẻ trung v&agrave; c&aacute; t&iacute;nh th&iacute;ch hợp với c&aacute;c ch&agrave;ng trai năng động</h3>\r\n<p>Thuộc d&ograve;ng&nbsp;<a title=\"Đồng hồ G-shock ch&iacute;nh h&atilde;ng, gi&aacute; rẻ, b&aacute;n tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dong-ho-deo-tay-g-shock\" target=\"_blank\">đồng hồ G-Shock</a>&nbsp;đến từ thương hiệu Casio - Nhật Bản,&nbsp;<a title=\"Đồng hồ nam G-Shock DW-5700BBM-2DR ch&iacute;nh h&atilde;ng, gi&aacute; rẻ, b&aacute;n tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dong-ho-deo-tay/g-shock-dw-5700bbm-2dr-nam\" target=\"_blank\">đồng hồ G-Shock DW-5700BBM-2DR</a>&nbsp;l&agrave; phụ kiện thời trang gi&uacute;p bạn thể hiện c&aacute; t&iacute;nh của m&igrave;nh.</p>', 1, 3208000, '0269eff057.jpg'),
(394, 'Đồng hồ Nam G-Shock GA-710B-1A4DR', 3, 32, '<h3>Thiết kế nam t&iacute;nh v&agrave; mạnh mẽ ph&ugrave; hợp với c&aacute;c ch&agrave;ng trai phong c&aacute;ch v&agrave; ưa vận động</h3>\r\n<p><a title=\"Đồng hồ nam G-Shock GA-710B-1A4DR ch&iacute;nh h&atilde;ng, gi&aacute; rẻ, b&aacute;n tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dong-ho-deo-tay/g-shock-ga-710b-1a4dr-nam\" target=\"_blank\">Đồng hồ nam G-Shock GA-710B-1A4DR</a>&nbsp;thuộc d&ograve;ng G-Shock của thương hiệu Casio nổi tiếng Nhật Bản.</p>', 1, 4319000, 'e7117cfc17.jpg'),
